    def ChangeImage(self):
        self.filename = QFileDialog.getOpenFileName()
        self.ImageLabel.setPixmap(QtGui.QPixmap(self.filename[0]))
        self.ChangeInfo()
        self.SaveButton.show()


    def ChangeInfo(self):
        self.img = Image.open(self.filename[0])
        self.OutputName.setText(str(self.filename[0].split('/')[-1])   )
        self.OutputWidth.setText(str(self.img.width))
        self.OutputHeight.setText(str(self.img.height))

    def CalculateImage(self):  
        if not self.InputWidth.text() or not self.InputHeight.text():
            self.ShowError()
        max_width,max_height = int(self.InputWidth.text()), int(self.InputHeight.text())
        width, height = self.img.width, self.img.height
        ratio = width/height
        if width > height:
            width = max_width
            height = int(width/ratio) 
        else:
            height = max_height
            width = int(height*ratio)
        self.width, self.height = width, height
        print(self.width, self.height)

    def ShowError(self):
        err = QMessageBox()
        if self.errorID:
            err.setWindowTitle("Input Error !")
            err.setText("Please enter the max width and height")
            err.setIcon(QMessageBox.Warning)
            x = err.exec_()
    

    def SaveImage(self):
        self.CalculateImage()
        self.img = self.img.resize((self.width,self.height), Image.ANTIALIAS)
        name = QFileDialog.getSaveFileName()
        name = name[0]
        print(name)
        self.img.save(name.split('/')[-1])

